            </svg>
          </label>
          <input
            name="password"
            id="password"
            className="lf--input"
            onChange={onChange}
            value={password}
            placeholder="Password"
            type="password"
          />
        </div>

        <button
          className="lf--submit"
          onClick={() => props.det({ email, password })}
        >
          SIGN UP
        </button>
        <br></br>
        <button className="lf--submit" onClick={() => SignUpGoogle()}>
          <i class="fab fa-google fa-2x">oogle</i>
        </button>
        {/* <input  className="lf--submit"type="submit"  value="LOGIN" /> */}
      </form>
    </div>
  );
};

export default connect(null, { det })(SignUp);
